import Table from 'react-bootstrap/Table'
import React, { useState } from 'react';
import Modal from 'react-bootstrap/Modal';
import { useTable } from 'react-table';



const customStyle={width:"85%", margin:"7%"};

const data = React.useMemo(
  () => [
    {cName: "Bello", co2: 32.0, distance: 1380, avgWeight: 1.4, 
      intensityFactor:0.21},
      {cName: "Mo", co2: 62.0, distance: 920, avgWeight: 0.4, 
      intensityFactor:0.71},
      {cName: "L", co2: 12.8, distance: 380, avgWeight: 1.0, 
      intensityFactor:0.31},
  ],
  []
)

const columns = React.useMemo(
  () => [
    {
      Header: 'Carrier Name',
      accessor: 'cname', // accessor is the "key" in the data
    },
    {
      Header: 'CO2',
      accessor: 'co2',
    },
    {
      Header: 'Distance',
      accessor: 'distance',
    },
    {
      Header: 'Avg. Shipments Weight',
      accessor: 'avgWeight',
    },
    {
      Header: 'Intensity Factor',
      accessor: 'intensityFactor',
    },
  ],
  []
)

const tableInstance = useTable({ columns, data });






