import Table from 'react-bootstrap/Table'
import React, { useState } from 'react';
import Modal from 'react-bootstrap/Modal';



const customStyle={width:"85%", margin:"7%"};

function handleRowClick(event){
  setTitle(event.target.parentNode.firstChild.innerHTML);
  setShow(true)


}



function OutTable(props){ //Name indicates table to be output.

  const [show, setShow] = useState(false);
  const [title,setTitle] = useState("");

  
    return(
      <div>
        <Table striped bordered hover size="sm" style={customStyle}>
  <thead>
    <tr>
      <th>Carrier Name</th>
      <th>CO2 Emission</th>
      <th>Distance</th>
      <th>Avg. Shipment Weight</th>
      <th>Intensity Factor</th>
    </tr>
  </thead>
  
  <tbody>
  {props.data.map((entry) => 
    <tr key={entry.cName} onClick={handleRowClick}>
      <td >{entry.cName}</td>
      <td>{entry.co2}</td>
      <td>{entry.distance}</td>
      <td>{entry.avgWeight}</td>
      <td>{entry.intensityFactor}</td>
    </tr>  
  )}
  </tbody>
</Table>
      <Modal
        show={show}
        onHide={() => setShow(false)}
        dialogClassName="modal-90w"
        aria-labelledby="example-custom-modal-styling-title"
      >
        <Modal.Header closeButton>
          <Modal.Title id="example-custom-modal-styling-title">
            
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <p>
            {props.content}
          </p>
        </Modal.Body>
      </Modal>

</div>
    );

}



export default OutTable;
