import React, { useState } from 'react';
import BTable from 'react-bootstrap/Table'
import Modal from 'react-bootstrap/Modal';
import Pagination from 'react-bootstrap/Pagination'
import PageItem from 'react-bootstrap/PageItem'
import axios from 'axios'; 

const customStyle={width:"85%", margin:"7%"};



class Table extends React.Component{
  state = {
    data: [],
    show: false,
    title: ""
  }

  componentDidMount(){
    //fetchData()
  }

  fetchData(query){
    let endpoint = 'http://localhost:8000/wel/?startDate='+query.startDate+
    '&endDate='+query.endDate

    if(query.typeOfCalculation){
      endpoint+='&typeOfCalculation='+query.typeOfCalculation
    }
    if(query.goodsType){
      endpoint+='&goodsType='+query.goodsType
    }
    if(query.startCity){
      endpoint+='&startCity='+query.startCity
    }
    if(query.endCity){
      endpoint+='&endCity='+query.endCity
    }
    if(query.orderParameter){
      endpoint+='&orderParameter='+query.orderParameter
    }
    
    axios.get(endpoint) 
        .then(res => { 
            data = res.data; 
            this.setState({ 
                data : data     
            }); 
        }) 
        .catch(err => {}) 


  }

  render(){
    return(
            <div>
              <BTable striped bordered hover size="sm" style={customStyle}>
        <thead>
          <tr>
            <th>Carrier Name</th>
            <th>CO2 Emission</th>
            <th>Distance</th>
            <th>Avg. Shipment Weight</th>
            <th>Intensity Factor</th>
          </tr>
        </thead>
        
        <tbody>
        {data.map((entry) => 
          <tr key={entry.cName} onClick={(event) => {
              this.setState({title: event.target.parentNode.firstChild.innerHTML, show: true})
          }}>
            <td >{entry.cName}</td>
            <td>{entry.co2}</td>
            <td>{entry.distance}</td>
            <td>{entry.avgWeight}</td>
            <td>{entry.intensityFactor}</td>
          </tr>  
        )}
        </tbody>
      </BTable>
            <Modal
              show={show}
              onHide={() => setShow(false)}
              dialogClassName="modal-90w"
              aria-labelledby="example-custom-modal-styling-title"
            >
              <Modal.Header closeButton>
                <Modal.Title id="example-custom-modal-styling-title">
                {title}
                </Modal.Title>
              </Modal.Header>
              <Modal.Body>
                <p>
                  This text is supposed to show the details of the carrier. Next is some lorum ipsum for
                  the aesthetic. Ipsum molestiae natus adipisci modi eligendi? Debitis amet quae unde
                  commodi aspernatur enim, consectetur. Cumque deleniti temporibus
                  ipsam atque a dolores quisquam quisquam adipisci possimus
                  laboriosam. Quibusdam facilis doloribus debitis! Sit quasi quod
                  accusamus eos quod. Ab quos consequuntur eaque quo rem! Mollitia
                  reiciendis porro quo magni incidunt dolore amet atque facilis ipsum
                  deleniti rem!
                </p>
              </Modal.Body>
            </Modal>
      
      </div>
          );
      
  }
}



