import React, { useState } from 'react';
import { TextField } from '@material-ui/core';




let now = new Date();
const styles={
    margin:"10%"
    // padding:"10%",
    // display:"inline-block"
}



class DateInput extends React.Component {
    constructor(props) {
      super(props);

      //Setting the default startDate to (now - 70 days) and endDate to now
      this.state = {startDate: new Date(new Date().setDate(new Date().getDate()-70)), 
        endDate: now};
    
      console.log("Constructor called")
      console.log(this.state);

    this.handleChange.bind(this);
    }

    handleChange(event) {
        console.log("Handler Called")
        const target = event.target;
        const value = target.value;
        const name = target.name;

        let newStartDate = new Date(value.toString());
    
        this.setState({
          [name]: new Date(value)}, () => {console.log(this.state)});

      }
      
  
    render() {
      return (
        <form>
            <TextField
            name="startDate"
            id="startDate"
            label="Start Date"
            type="date"
            defaultValue={
                (new Date(new Date().setDate(new Date().getDate()-70))).toISOString().substring(0,10) }
            InputLabelProps={{shrink: true}}
            onChange={this.handleChange.bind(this)}
            style={styles}
  />
              <TextField
            name="endDate"
            id="endDate"
            label="End Date"
            type="date"
            defaultValue={now.toISOString().substring(0,10)}
            InputLabelProps={{shrink: true}}
            onChange={this.handleChange.bind(this)}
            style={styles}
  />

        </form>
      );
    }
  }

  export default DateInput;
