import React, { useState } from 'react';
import { TextField } from '@material-ui/core';
import Button from 'react-bootstrap/Button';
import Collapse from 'react-bootstrap/Collapse'
import OutTable from "./../Components/Table"
import FilterListIcon from '@material-ui/icons/FilterList';
import Modal from 'react-bootstrap/Modal';
import FiltersInput from "./../Components/FiltersInput.js";
import DropdownButton from 'react-bootstrap/DropdownButton'
import Dropdown from 'react-bootstrap/Dropdown'




const data = [{cName: "Bello", co2: 32.0, distance: 1380, avgWeight: 1.4, 
      intensityFactor:0.21},{cName: "Mo", co2: 62.0, distance: 920, avgWeight: 0.4, 
      intensityFactor:0.71},{cName: "L", co2: 12.8, distance: 380, avgWeight: 1.0, 
      intensityFactor:0.31}]





let now = new Date();



class DateInput extends React.Component {
    constructor(props) {
      super(props);

      //Setting the default startDate to (now - 70 days) and endDate to now
      this.state = {
        startDate: new Date(new Date().setDate(new Date().getDate()-70)), 
        endDate: now,
        startCity: "",
        endCity: "",
        goodsType: "",
        typeOfCalculation: "",
        orderParameter: "",
      displayed: false, //this handles displaying the resulting table
      filtersDisplay: false}; //this handles displaying the Filter&Sort form
      // console.log("Constructor called")
      // console.log(this.state);
      // console.log(props);

    this.handleDisplay.bind(this);
    this.handleQuery.bind(this);
    this.handleFiltersDisplay.bind(this);
    // this.render.bind(this);
    }

    handleDisplay(event){
      console.log("Display Handler called, state will now be changed")
      this.setState({displayed: !this.state.displayed});
      console.log(this.state.displayed);
    }

    handleQuery(event) {

        console.log("Handler Called")

        const target = event.target;
        const value = target.value;
        const name = target.name;

        this.setState({
          [name]: new Date(value)}, () => {console.log(this.state)});

      }


      handleFiltersDisplay(event){
        
      }
      
      
  
    render() {
      return (
        <div >
        <form >

            <TextField
            name="startDate"
            id="startDate"
            label="Start Date"
            type="date"
            defaultValue={
                (new Date(new Date().setDate(new Date().getDate()-70))).toISOString().substring(0,10) }
            InputLabelProps={{shrink: true}}
            onChange={this.handleQuery.bind(this)}
            style={{margin:"7% 3% 0"}}
  />
              <TextField
            name="endDate"
            id="endDate"
            label="End Date"
            type="date"
            defaultValue={now.toISOString().substring(0,10)}
            InputLabelProps={{shrink: true}}
            onChange={this.handleQuery.bind(this)}
            style={{margin:"7% 3% 0"}}
  />

              <FilterListIcon style={{margin:"7.5% 3% 0 0"}} onClick = {()=>{this.setState({filtersDisplay: !this.state.filtersDisplay})
              console.log("FilterDIsplay: "+this.state.filtersDisplay)}}/>
        

         <Button variant="outline-primary" size="lg" style={{margin:"7.5% 2.5% 0"}} onClick={this.handleDisplay.bind(this)}>Search</Button>

        </form>
        <Collapse in={this.state.filtersDisplay}>

        <div>
        {/* <FiltersInput show={this.state.filtersDisplay} onHide={() => this.setState({filtersDisplay: !this.state.filtersDisplay})} /> */}
        <Modal
        show={this.state.filtersDisplay}
        size="md"
        aria-labelledby="contained-modal-title-vcenter"
        centered
      >
        <Modal.Header closeButton>
          <Modal.Title id="contained-modal-title-vcenter">
            Filter & Sort
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <h4>Filters</h4>
          <TextField onChange = {this.handleQuery.bind(this)}name="startCity" style = {{margin:"0 1% 3% 1%"}} id="outlined-basic" label="Start City" variant="outlined" />
          <TextField onChange = {this.handleQuery.bind(this)}name="endCity" style = {{margin:"0 1% 3% 1%"}} id="outlined-basic" label="End City" variant="outlined" />
          <DropdownButton style = {{margin:"5% 1%", display:"inline"}} id="dropdown-basic-button" title="Type Of Calculation" variant = "secondary">
            <Dropdown.Item name="typeOfCalculation" onClick={this.handleQuery.bind(this)}>Default</Dropdown.Item>
            <Dropdown.Item name="typeOfCalculation" onClick={this.handleQuery.bind(this)}>Modeled</Dropdown.Item>
            <Dropdown.Item name="typeOfCalculation" onClick={this.handleQuery.bind(this)}>Primary</Dropdown.Item>
        </DropdownButton>
        <DropdownButton style = {{margin:"5% 11%", display:"inline"}} id="dropdown-basic-button" title="Type Of Goods" variant = "secondary">
            <Dropdown.Item name="goodsType" onClick={this.handleQuery.bind(this)}>Cereals</Dropdown.Item>
            <Dropdown.Item name="goodsType" onClick={this.handleQuery.bind(this)}>Chemicals</Dropdown.Item>
            <Dropdown.Item name="goodsType" onClick={this.handleQuery.bind(this)}>Containers</Dropdown.Item>
        </DropdownButton>
        <h4 style ={{marginTop:"9%"}}>Sort On</h4>
        <DropdownButton style = {{margin:"5% 1%", display:"inline"}} id="dropdown-basic-button" title="Column" variant = "secondary">
            <Dropdown.Item name="orderParameter" onClick={this.handleQuery.bind(this)}>Name</Dropdown.Item>
            <Dropdown.Item name="orderParameter" onClick={this.handleQuery.bind(this)}>CO2 Emission</Dropdown.Item>
            <Dropdown.Item name="orderParameter" onClick={this.handleQuery.bind(this)}>Distance</Dropdown.Item>
            <Dropdown.Item name="orderParameter" onClick={this.handleQuery.bind(this)}>Avg. Shipment Weight</Dropdown.Item>
            <Dropdown.Item name="orderParameter" onClick={this.handleQuery.bind(this)}>Intensity Factor</Dropdown.Item>

        </DropdownButton>



        </Modal.Body>
        
        <Modal.Footer>
          <Button onClick={() => this.setState({filtersDisplay: !this.state.filtersDisplay})}>Close</Button>
        </Modal.Footer>
      </Modal>

        </div>
      </Collapse>

        {/* <Collapse in={this.state.displayed}>
        <div>
        <OutTable data = {data} />
        </div>
      </Collapse> */}
        </div>
      );
    }
  }

  export default DateInput;
